package com.example.groupproject.dataProfileFunction;
import com.example.groupproject.dataProfileFunction.ProfileActivity;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.appcompat.app.AppCompatActivity;
import android.app.Activity;
import android.content.Intent;
import com.example.groupproject.R;
import android.net.Uri;
import android.os.Bundle;
import android.provider.MediaStore;
import android.util.Log;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.Toast;
import android.app.AlertDialog;
import com.google.android.gms.tasks.OnFailureListener;
import com.google.android.gms.tasks.OnSuccessListener;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.google.firebase.firestore.DocumentReference;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.storage.FirebaseStorage;
import com.google.firebase.storage.StorageReference;
import com.squareup.picasso.Picasso;
import android.content.DialogInterface;
import java.util.HashMap;
import java.util.Map;

/**
 *This EditActivity allows users to edit their profile information,
 * including their full name and profile image. It receives the current
 * full name and profile image URI as extras from the ProfileActivity.
 * Users can choose to update their profile image either by selecting from
 * default images or from the gallery. Upon saving, the updated information
 * is stored in Firestore, edits updated immediately and the user is redirected back to the ProfileActivity.
 * authored and implemented the complete feature requirement end to end
 * @author ${Tohfa Siddika Barbhuiya}
 */

public class EditActivity extends AppCompatActivity {

    public static final String TAG = "TAG";
    EditText profileFullName;
    ImageView profileImageView;
    Button saveBtn;
    FirebaseAuth fAuth;
    FirebaseFirestore fStore;
    FirebaseUser user;
    StorageReference storageReference;
    int selectedImageIndex = 0; // Default selected image index

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_edit);

        Intent data = getIntent();
        final String fullName = data.getStringExtra("fullName");

        /**
         * Verified that data used in this file is persisted in firebase
         * @Verified by:- Muhammad Arslan Amjad Qureshi
         */

        fAuth = FirebaseAuth.getInstance();
        fStore = FirebaseFirestore.getInstance();
        user = fAuth.getCurrentUser();
        storageReference = FirebaseStorage.getInstance().getReference();

        profileFullName = findViewById(R.id.profileFullName);
        profileImageView = findViewById(R.id.profileImageView);
        saveBtn = findViewById(R.id.saveProfileInfo);

        // Load default profile image
        String profileImageUri = getIntent().getStringExtra("profileImageUri");
       // String defaultImageUri = getDefaultImageUri(selectedImageIndex);
        //Picasso.get().load(defaultImageUri).into(profileImageView);

        // Click listener for profile image to choose from default images or gallery
        profileImageView.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // Show dialog to choose image source
                showImageSourceDialog();
            }
        });

        saveBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                if (profileFullName.getText().toString().isEmpty()) {
                    Toast.makeText(EditActivity.this, "Name cannot be empty.", Toast.LENGTH_SHORT).show();
                    return;
                }

                // Update name and profile image in Firestore
                DocumentReference docRef = fStore.collection("users").document(user.getUid());
                Map<String, Object> edited = new HashMap<>();
                edited.put("name", profileFullName.getText().toString());
                edited.put("image", getDefaultImageUri(selectedImageIndex));

                docRef.update(edited).addOnSuccessListener(new OnSuccessListener<Void>() {
                    @Override
                    public void onSuccess(Void aVoid) {
                        Toast.makeText(EditActivity.this, "Profile Updated", Toast.LENGTH_SHORT).show();
                        startActivity(new Intent(getApplicationContext(), ProfileActivity.class));
                        finish();
                    }
                }).addOnFailureListener(new OnFailureListener() {
                    @Override
                    public void onFailure(@NonNull Exception e) {
                        Toast.makeText(EditActivity.this, "Failed to update profile.", Toast.LENGTH_SHORT).show();
                    }
                });
            }
        });

        profileFullName.setText(fullName);

        Log.d(TAG, "onCreate: " + fullName);
    }

    // Method to show dialog to choose image source
    private void showImageSourceDialog() {
        AlertDialog.Builder builder = new AlertDialog.Builder(this);
        builder.setTitle("Choose Image Source");
        builder.setItems(new CharSequence[]{"Default Images", "Gallery"}, new DialogInterface.OnClickListener() {
            @Override
            public void onClick(DialogInterface dialog, int which) {
                switch (which) {
                    case 0:
                        // Choose from default images
                        showDefaultImageDialog();
                        break;
                    case 1:
                        // Choose from gallery
                        Intent openGalleryIntent = new Intent(Intent.ACTION_PICK, MediaStore.Images.Media.EXTERNAL_CONTENT_URI);
                        startActivityForResult(openGalleryIntent, 1001);
                        break;
                }
            }
        });
        builder.show();
    }

    // Method to show dialog to choose from default images
    private void showDefaultImageDialog() {
        AlertDialog.Builder builder = new AlertDialog.Builder(this);
        builder.setTitle("Choose Default Image");
        builder.setItems(new CharSequence[]{"Image 1", "Image 2", "Image 3", "Image 4"}, new DialogInterface.OnClickListener() {
            @Override
            public void onClick(DialogInterface dialog, int which) {
                selectedImageIndex = which;
                String defaultImageUri = getDefaultImageUri(selectedImageIndex);
                Picasso.get().load(defaultImageUri).into(profileImageView);
            }
        });
        builder.show();
    }

    // Method to get the URI of a default image based on index
    private String getDefaultImageUri(int index) {
        String[] defaultImageUris = {
                "",
                "",
                "",
                ""

        };

        // Check if the index is within the bounds of the array
        if (index >= 0 && index < defaultImageUris.length) {
            // Return the URI corresponding to the index
            return defaultImageUris[index];
        } else {
            // If the index is out of bounds, return an empty string
            return "";
        }
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
        super.onActivityResult(requestCode, resultCode, data);
        if (requestCode == 1001 && resultCode == Activity.RESULT_OK && data != null) {
            // Load the selected image from gallery
            Uri imageUri = data.getData();
            Picasso.get().load(imageUri).into(profileImageView);
        }
    }
}